# Constant for the number of space vehicles
use constant SV_COUNT => 1;

print ("Version: alm_download.pl#2\n\n");

################################################################################
# Set the COM port from command line argument, or default to old value
################################################################################
$comport = $ARGV[0];
if(not defined ($comport))
{
  $comport = 9;
}


################################################################################
# Download the alamanc files.
################################################################################
